function popDiv(theDiv, theTxt) {
	document.getElementById(theDiv).innerHTML = theTxt;
}
function popForm(theField, theVar) {
	document.getElementById(theField).value = theVar;
}
function getDiv(theDiv) {
	var theTxt = document.getElementById(theDiv).innerHTML
	return theTxt;
}

function sendFunc(theFunc, theParam, theDiv) {
	document.getElementById(theDiv).applyFunc(theFunc, theParam, theDiv);
}
var keyShortsState = false;
function showKeyShorts() {
	var theDiv = document.getElementById('keyShorts');
	if(keyShortsState) {
		theDiv.style.display = 'none'
		theDiv.style.visibility = 'hidden';
		keyShortsState = false
	} else {
		theDiv.style.display = 'block'
		theDiv.style.visibility = 'visible';
		keyShortsState = true
	}
}
function showDiv(theDiv, theState) {
	var theDiv = document.getElementById(theDiv);
	if(theState) {
		theDiv.style.display = 'block'
		theDiv.style.visibility = 'visible';
	} else {
		theDiv.style.display = 'none'
		theDiv.style.visibility = 'hidden';
	}	
}
function showDivCl(theDiv, theState) {
	var theDiv = document.getElementsByClassName(theDiv)[0];
	if(theState) {
		theDiv.style.display = "block"
		theDiv.style.visibility = 'visible';
	} else {
		theDiv.style.display = "none"
		theDiv.style.visibility = 'hidden';
	}	
}

function resizeDiv(theSect, newH){
	var theDiv;
	if(theSect == 0){ 
	   	theDiv = document.getElementById('vidPlayer');
	} else if (theSect == 1) {
		theDiv = document.getElementById('vidMenu');
	}
	theDiv.style.height = newH+'px';
}
function setFocus(theInt) {
	if(theInt == 0) {
		document.getElementById('vidPlayer').focus();
	} else if(theInt == 1){
		document.getElementById('vidMenu').focus();
	} else if(theInt == 3) {
		document.getElementById('embedContent').focus();
	} 
}

document.getElementsByClassName = function(){
	if(document.hasChildNodes && arguments[0]){
		var data = new Array();
		for(a=0;a<document.getElementsByTagName("*").length;a++){
			if(document.getElementsByTagName("*")[a].className == arguments[0]){
				data.push(document.getElementsByTagName("*")[a]);
			}
		}
		return data;
	}
}


